import React, { Component } from 'react';

import './App.css';
import { render } from '@testing-library/react';

class App extends Component {
  constructor(props) {
    super(props);
    console.log("This is my initializer")

    const movies = [
      {id: 0, title: "Shutter Island", release_date:"2010-02-14", overview: "World War II soldier-turned-U.S. Marshal Teddy Daniels investigates the disappearance of a patient from a hospital for the criminally insane, but his efforts are compromised by his troubling visions and also by a mysterious doctor."},
      {id: 1, title: "Avengers", release_date:"2011-32-14", overview: "test"},
    ]


    this.state = {rows: [
      <p key="1">This is my row0</p>,
      <p key="2">This is my row1</p>,
      <p key="3">This is my row2</p>,
      <p key="4">This is my row3</p>,
    ]}

    var movieRows = []

    movies.forEach((movie) => {
      console.log(movie.title)
      movieRows.push(<p>Movie Title: {movie.title}</p>)
    })

    this.state = {movieRows: movieRows}

  }


  render() {
    return (
      <div className="App">
        <table className = "titleBar">
          <tbody>
            <tr>
              <td width = "8">
                <img alt = "app icon" width = "100" src = "DSC01148.JPG"/>
              </td>
              <h1>
                Favorite Movies
              </h1>
            </tr>
          </tbody>
        </table>


        <input style = {{
          fontSize: 24,
          display: 'block',
          width: "99%",
          paddingTop: 8,
          paddingBottom: 8,
          paddingLeft: 16
      }}placeholder = "Enter movie title..."/>

        {this.state.rows}

      </div>
    );
  }
}

export default App;
