            </tr>
          </tbody>
        </table>


        <input style = {{

          fontSize: 24,
          width: "50%",
          paddingTop: "1%",
          paddingBottom: "1%",

      }} onChange={this.searchChangeHandler.bind(this)} placeholder = "Enter movie title..."/>

        {this.state.rows}

      <img width = "500" src = "ArtGal.png" />
      </div>
    );
  }
}

export default App;
