import React, { Component } from 'react';

import './App.css';
import { render } from '@testing-library/react';

class App extends Component {
  constructor(props) {
    super(props);
    console.log("This is my initializer")

    const movies = [
      {id: 0, poster_src: "https://image.tmdb.org/t/p/w188_and_h282_bestv2/RYMX2wcKCBAr24UyPD7xwmjaTn.jpg", title: "Shutter Island", release_date:"2010-02-14", overview: "World War II soldier-turned-U.S. Marshal Teddy Daniels investigates the disappearance of a patient from a hospital for the criminally insane, but his efforts are compromised by his troubling visions and also by a mysterious doctor."},
      {id: 1, poster_src: "https://image.tmdb.org/t/p/w188_and_h282_bestv2/or06FN3Dka5tukK1e9sl16pB3iy.jpg", title: "Avengers", release_date:"2011-32-14", overview: "test"},
    ]

    var movieRows = []

    movies.forEach((movie) => {
      console.log(movie.title)
      const movieRow = <table key = {movie.id}>
        <tbody>
          <tr>
            <td>
              <img alt = "poster" width = "120" src = {movie.poster_src}/> 
            </td>
            <td>
              {movie.title}
              <p>{movie.overview}</p>
            </td>
          </tr>
        </tbody>
      </table>
      movieRows.push(movieRow)
    })

    this.state = {rows: movieRows}

  }


  render() {
    return (
      <div className="App">
        <table className = "titleBar">
          <tbody>
            <tr>
              <td width = "8">
                <img alt = "app icon" width = "100" src = "DSC01148.JPG"/>
              </td>
              <h1>
                Favorite Movies
              </h1>
            </tr>
          </tbody>
        </table>


        <input style = {{
          fontSize: 24,
          display: 'block',
          width: "99%",
          paddingTop: 8,
          paddingBottom: 8,
          paddingLeft: 16
      }}placeholder = "Enter movie title..."/>

        {this.state.rows}

      </div>
    );
  }
}

export default App;
